import React, {Component} from 'react';
import Header from './header';
import Footer from './footer';

const TESTS = 'http://localhost:3004/tests';

const generateQuiz = ({test}) => {
    if (test) {
        console.log(test);
        return (

            <div className="container">
                {test.map((data) => {
                    return (
                        <div className="row mx-0 my-5" key={data.id}>
                            <h2 className="display-5 text-center font-weight-bold mt-5 w-100">{data.name}</h2>
                            <div className="col-sm-10 col-md-8 mx-auto">
                                <p className="text-muted text-center font-italic"><small>Instructions: Answer all questions.</small></p>
                                {/* {data.map((que) => {
                                    return (
                                        <div>
                                            This is a question
                                        </div>
                                    )
                                })} */}

                            </div>
                        </div>
                    )
                })}
            </div>
        )
    }
}

class Test extends Component {
    constructor (props) {
        super (props);
        this.state = {
            test: ''
        }
    }

    componentDidMount () {
        fetch (`${TESTS}?id=${this.props.match.params.id}`, {method: 'GET'})
        .then (response => response.json())
        .then (json => {
            this.setState({
                test: json
            })   
        })
    }

    render() {
        console.log(this.state);
        return (
            <div>
                <Header />
                {generateQuiz(this.state)}
                <Footer />
            </div>
        )
    }
}

export default Test;
