import React, {Component} from 'react';
import Header from "./header";
import Footer from './footer';
import Modules from './modules';

const DATA = 'http://localhost:3004/modules';

const displayModule = ({data}) => {
    if (data) {
        return (
            <div className="reading row mx-0">
                {data.map((reading) => {
                    console.log(reading);
                    return (
                        <div className="col-sm-10 col-md-8 py-5 mx-auto" key={reading.id}>
                            <h1 className="text-center font-weight-bold font-piedra display-5">
                                {reading.title}
                            </h1>
                            <img className="banner img-thumbnail shadow" src={require(`../images/${reading.img}`)} alt=""/>
                        </div>
                    )   
                })}
            </div>
        )
    }
}

class Readings extends Component {
    constructor (props) {
        super(props);
        this.state = {
            data: ''
        }
    }

    componentDidMount() {
        fetch (`${DATA}?id=${this.props.match.params.id}`, {method: 'GET'})
        .then (response => response.json())
        .then (json => {
            this.setState({
                data: json
            })
        })
    }

    render () {
        console.log(this.props);
        return (
            <div>
                <Header />
                {displayModule(this.state)}
                <Footer />
            </div>
        )
    }
}

export default Readings;
