import React, {Component} from 'react';

class Timer extends Component {
    constructor (props) {
        super (props);
        this.state = {
            time: 10 * 60 & 1000,       // for 10 minutes
            hours: 0,
            minutes: 0,
            seconds: 0
        }
    }

    render () {
        const {hours, minutes, seconds} = this.state;
        return (
            <div className="row">
                <table>
                    <tr>
                        <th>Hours</th>
                        <th>Minutes</th>
                        <th>Seconds</th>
                    </tr>
                    <tr>
                        <th>{hours}</th>
                        <th>{minutes}</th>
                        <th>{seconds}</th>
                    </tr>
                </table>
            </div>
        )
    }

    componentDidMount () {
        const {time, hours, minutes, seconds} = this.state;
        this.interval = setInterval(() => {
            this.setState({
                seconds: Math.floor(time / 1000) % 60,
                minutes: Math.floor(time / 60) % 60,
            })
        }, 1000)
    }
}

export default Timer;
