import React from 'react';

const Login = () => {
    return (
        <div className="loginBanner container-fluid">
            <div className="row justify-content-center align-items-center h-100">
                <div className="loginDiv bg-warning col-10 col-sm-6 col-md-5 mx-auto">
                    <form className="d-flex flex-column justify-content-center align-items-center py-4" action="#">
                        <img className="logo rounded-circle" src={require('../images/globe.png')} alt="logo" />
                        <input className="w-100 bg-transparent py-2 my-2" type="text" placeholder="Username" />
                        <input className="w-100 bg-transparent py-2 my-2" type="password" placeholder="Password" />
                        <button className="btn btn-md btn-outline-dark my-3 px-md-4">Log in</button>
                    </form>
                    <p className="text-dark px-sm-3 font-italic text-center"><small>By logging in, I agree to the terms and conditions of the University of Ilorin.</small></p>
                </div>
            </div>
        </div>
    )
}

export default Login;
