    }

    componentDidMount() {
        fetch (`${DATA}?id=${this.props.match.params.id}`, {method: 'GET'})
        .then (response => response.json())
        .then (json => {
            this.setState({
                data: json
            })
        })
    }

    render () {
        console.log(this.props);
        return (
            <div>
                <Header />
                {displayModule(this.state)}
                <Footer />
            </div>
        )
    }
}

export default Readings;
