import React from 'react';

const generateBlocks = ({modules}) => {
    if (modules) {
        return (
            <div className="moduleCard row p-3 p-md-5">
                {modules.map((item) => {
                    return (
                        <div key={item.id} className="col-sm-6 col-md-4 my-3">
                            <div className="card shadow">
                                <img className="card-img-top" src={require(`../images/${item.img}`)} alt={item.name} />
                                <div className="card-overlay rounded-top d-flex justify-content-center align-items-center text-warning">
                                    <i className={`fa ${item.fontAwesome}`}></i>
                                    <p>{item.title}</p>
                                </div>
                                <div className="card-body">
                                    <p className="text-center card-text">{item.name}</p>
                                </div>
                            </div>
                        </div>
                    )
                })}
            </div>
        )
    }   
}

const Module = (props) => {
    return (
        <div>
            {generateBlocks(props)}
        </div>
    )
}

export default Module;
