    constructor (props) {
        super(props);
        this.state = {
            data: ''
        }
    }

    componentDidMount() {
        fetch (DATA, {method: 'GET'})
        .then (response => response.json())
        .then (json => {
            this.setState({
                data: json
            })
        })
    }

    render () {
        return (
            <div>
                <Header />
                {/* <Reading data={this.state.data} /> */}
